import React, { useState } from "react";
import SearchFilters from "./SearchFilters";
import ResultsTable from "./ResultsTable";
import SelectedContact from "./SelectedContact";
import contacts from "./contact.json";

const App = () => {
  const [filteredContacts, setFilteredContacts] = useState(contacts);
  const [selectedContact, setSelectedContact] = useState(null);

  const handleSearch = (filters) => {
    console.log(filters);
    const results = contacts.filter((contact) => {
      return (
        (!filters.firstName || (contact.firstName && contact.firstName.toLowerCase().includes(filters.firstName.toLowerCase()))) &&
        (!filters.lastName || (contact.lastName && contact.lastName.toLowerCase().includes(filters.lastName.toLowerCase()))) &&
        (!filters.dob || contact.dob === filters.dob) &&
        (!filters.address || 
          (!filters.address.street || (contact.address && contact.address.street && contact.address.street.toLowerCase().includes(filters.address.street.toLowerCase()))) &&
          (!filters.address.city || (contact.address && contact.address.city && contact.address.city.toLowerCase().includes(filters.address.city.toLowerCase()))) &&
          (!filters.address.state || (contact.address && contact.address.state && contact.address.state.toLowerCase().includes(filters.address.state.toLowerCase()))) &&
          (!filters.address.zip || (contact.address && contact.address.zip && contact.address.zip.toLowerCase().includes(filters.address.zip.toLowerCase())))
        ) &&
        (!filters.email || (contact.email && contact.email.toLowerCase().includes(filters.email.toLowerCase()))) &&
        (!filters.phone || (contact.phone && contact.phone.includes(filters.phone)))
      );
    });

    setFilteredContacts(results);
  };

  return (
    <div>
      <SearchFilters onSearch={handleSearch} />
      <ResultsTable contacts={filteredContacts} onSelectContact={setSelectedContact} />
      {selectedContact && <SelectedContact contact={selectedContact} />}
    </div>
  );
};

export default App;
